package com.myfirst.test.collectionrnd;

public class SwapNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=30;
		
		System.out.println("a="+a+" b="+b);
		
		a=a+b;
		b=a-b;
		a=a-b;
		//b=(a+b)/2;
		
		System.out.println("a="+a+" b="+b);
	}

}
